INSERT INTO `t_ac_prod` VALUES (2,105,'Toshiba','','/upfiles/image/123201738.jpg','Toshiba','2009-01-24 03:17:38'),(3,105,'霍尼威尔','','/upfiles/image/123201753.jpg','霍尼威尔','2009-01-24 03:17:53'),(4,105,'江森1','','/upfiles/image/12320188.jpg','江森1','2009-01-24 03:18:08'),(5,105,'金日','','/upfiles/image/123201841.jpg','金日','2009-01-24 03:18:41'),(6,105,'麦克威尔','','/upfiles/image/123201915.jpg','麦克威尔','2009-01-24 03:19:15'),(7,105,'明新','','/upfiles/image/123201926.jpg','明新','2009-01-24 03:19:26'),(8,105,'上海凯泉2','','/upfiles/image/123201938.jpg','上海凯泉2','2009-01-24 03:19:38'),(9,105,'上海凯泉','','/upfiles/image/123201952.jpg','上海凯泉','2009-01-24 03:19:52'),(10,105,'上海连成','','/upfiles/image/12320203.jpg','上海连成','2009-01-24 03:20:03'),(11,105,'斯频德','','/upfiles/image/123202015.jpg','斯频德','2009-01-24 03:20:15'),(12,105,'西门子','','/upfiles/image/123202030.jpg','西门子','2009-01-24 03:20:30'),(13,105,'约克','','/upfiles/image/123202038.jpg','http://www.sohu.com','2009-02-12 18:59:42'),(14,5,'北京市人大常委会','','/upfiles/image/21214211.jpg','无','2009-02-12 21:21:01'),(15,5,'北航举重馆','','/upfiles/image/212142310.jpg','无','2009-02-12 21:32:59'),(16,5,'罗森伯格亚太电子有限公司','','/upfiles/image/21214265.jpg','无','2009-02-12 21:32:06'),(17,5,'实验动物繁育中心','','/upfiles/image/21214411.jpg','无','2009-02-12 21:41:01'),(18,12,'DBFP系列变风量空调机组结构及技术特点','&nbsp;\r\n<p>\r\n	<b><span style=\"font-family:宋体;\">概述<span></span></span></b>\r\n</p>\r\n<p style=\"text-indent:31.8pt;\">\r\n	<span style=\"font-family:宋体;\">通惠<span>-</span>开利提供的<span>DBFP</span>空调机组适用于工业、民用建筑的空气调节。该产品是在引进国外公司技术的基础上，为满足当前国内外建筑物层高紧凑的趋势而专门设计的超薄型空调机组。盘管翅片采用开利最新翅片型<span>,</span>，热效率大幅度提高。表冷器主体采用优质铜管与铝箔并经先进的机械胀管工艺使之成为紧密一体后，经<span>2.8MPa</span>气压密封测试，保证了其优异的性能，冷量优于国内外同类产品。同时该机组采用低噪声外转子离心通风机<span>,</span>使机组达到高效率、低噪声的运行要求。箱体结构采用无框架结构形式，为优质镀锌钢板制造。箱体内敷贴消声保温材料，有强度高、保温性好及低噪声的优点。<span></span></span>\r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:宋体;\">风机<span></span></span></b>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;\"><span>&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-family:宋体;\">采用外转子离心通风机，它具有结构紧凑、振动小、噪声低、效率高、气流稳定等的优点。同时可配备各种变频调速控制器，实现无级调速，以适应系统负荷多变的要求，实现变风量节能运行。<span></span></span>\r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:宋体;\">盘管<span></span></span></b>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;\"><span>&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-family:宋体;\">盘管工作压力为<span>1.6MPa </span>，而测试压力高达<span>2.8MPa</span>，依据美国暖通冷冻空调工程师协会<span>ARI</span>规范制造，盘管的能量、风量、水压降均符合<span>ARI</span>标准。冷水盘管采用紫铜管和铝箔制造，铝翅片为双曲正弦波纹，在保证具有较高的换热系数的同时，具有较小的空气阻力，同时特殊的翅片结构加强了气流的扰动，提高了热交换效率。翅片和铜管用机械加压胀接，盘管四周有镀锌钢板框。所有盘管均采用逆流方式。<span></span></span>\r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:宋体;\">过滤器</span></b><span style=\"font-family:宋体;\"><span>&nbsp; </span></span>\r\n</p>\r\n<p style=\"text-indent:31.5pt;\">\r\n	<span style=\"font-family:宋体;\">机组特别设计的可拆式专用板式过滤器可以从机组左右两侧方向方便地折叠抽出，所以特别适用于空间紧凑场合的维护清洗。 <span></span></span>\r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:宋体;\">水盘<span></span></span></b>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;\"><span>&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-family:宋体;\">机组精心设计的内水盘和特厚内衬保温材料及联体外水盘保证了机组在恶劣环境工况下也无凝露滴水现象，故特别适用于吊装使用。<span></span></span>\r\n</p>','/upfiles/image/21215054.jpg','适用于工业、民用建筑的空气调节。该产品是在引进国外公司技术的基础上，为满足当前国内外建筑物层高紧凑的趋势而专门设计的超薄型空调机组','2009-02-12 22:32:25'),(19,12,'BFP系列变风量空调机组结构及技术特点','&nbsp;\r\n<p>\r\n	<b><span style=\"font-family:宋体;\">概述<span></span></span></b>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;\"><span>&nbsp;&nbsp;&nbsp; </span>BFP</span><span style=\"font-family:宋体;\">系列空调机组适用于工业、民用建筑的全新风空气调节，可随意增加功能段以满足降温、降湿、加热和加湿等的要求。盘管翅片采用开利九十年代最新翅片型，换热效率大幅度提高，冷量优于国内外同类产品。同时该机组采用低噪声外转子离心通风机<span>,</span>使机组达到高效率、低噪音的运行要求。<b><span></span></b></span>\r\n</p>\r\n<p style=\"text-indent:26.25pt;\">\r\n	<span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:宋体;\">箱体<span></span></span></b>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;\"><span>&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-family:宋体;\">机组采用可拆装的铝合金框架结构，箱体结构为双层面板，内外钢板间为发泡聚氨脂密封材料，能在不损伤保温材料的前提下，随意进入内部进行检修保养。极具灵活性的模数化结构体系是基于标准尺寸的面板和框架结构，没有暴露于箱体外的紧固件。标准的检修门，可迅速拆卸的面板，可以重新组合或更换。这种灵活性很强的结构给在比较困难的场合进行现场安装带来诸多方便。<span></span></span>\r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:宋体;\">风机<span></span></span></b>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;\"><span>&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-family:宋体;\">采用外转子离心通风机，它具有结构紧凑、振动小、噪声低、效率高、气流稳定等的优点。同时可配备各种变频调速控制器，实现无极调速，以适应系统负荷多变的要求。<span></span></span>\r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:宋体;\">盘管<span></span></span></b>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;\"><span>&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-family:宋体;\">盘管工作压力为<span>1.6MPa </span>，而测试压力高达<span>2.8MPa</span>，依据美国暖通冷冻空调工程师协会<span>ARI</span>规范制造，盘管的能量、风量、水压降均符合<span>ARI</span>标准。冷水盘管采用紫铜管和铝箔制造，铝翅片为双曲正弦波纹，在保证具有较高的换热系数的同时，具有较小的空气阻力，同时特殊的翅片结构加强了气流的扰动，提高了热交换效率。翅片和铜管用机械加压胀接，盘管四周有镀锌钢板框。凝水盘置于支承导轨上，便于抽出。水盘下粘贴有聚苯乙烯保温材料，保证无冷凝水沉积，减轻了凝水盘的腐蚀又避免了水盘内滋长细菌，保证了室内空气品质。所有盘管均采用逆流方式。<span></span></span>\r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:宋体;\">过滤器<span></span></span></b>\r\n</p>\r\n<p style=\"text-align:left;\" align=\"left\">\r\n	<span style=\"font-family:宋体;\"><span>&nbsp;&nbsp;&nbsp;&nbsp; </span>BFP</span><span style=\"font-family:宋体;\">机组板式初效过滤器的滤料采用可清洗的锦纶凹凸网，更换方便，便于用户清洗。同时也有板式无纺布初效过滤和其他中效过滤可供选择。<span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;\"></span>\r\n</p>','/upfiles/image/21215226.jpg','BFP系列空调机组适用于工业、民用建筑的全新风空气调节，可随意增加功能段以满足降温、降湿、加热和加湿等的要求。','2009-03-20 20:42:00'),(20,12,'42系列风机盘管机组结构及技术特点','&nbsp;\r\n<p>\r\n	<span style=\"font-family:宋体;\">概述<span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;\"><span>&nbsp;&nbsp;&nbsp; </span>42</span><span style=\"font-family:宋体;\">系列风机盘管机组，其冷热源由外界集中供应，当冷热源提供给盘管后，室内空气由风机吸入机内，经盘管冷却（加热），再送回室内，经机组不断循环，使房间温度得到调节。<span>42</span>系列风机盘管机组对风机、盘管、电机等关键零部件精心设计而推出的具有超高效率、超低噪声等特点，且能最大限度满足市场需要的风机盘管。机组主要由风机、盘管、凝水盘和箱体等部件组成。另外可以根据用户的要求配置自控配件。<span></span></span>\r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:宋体;\">风机<span></span></span></b>\r\n</p>\r\n<p style=\"text-indent:26.25pt;\">\r\n	<span style=\"font-family:宋体;\">采用前向多翼金属型离心叶轮，叶片采用整体冲制工艺，并采用国内首创镀锌板带锌焊接技术，轴盘采用锌合金带六角定位压铸工艺，再加上先进的动平衡试验，使叶轮生产率高，质量好，而且金属叶轮能达到防火安全性能。双吸前向多翼离心式风机<span>,</span>具有耗电省、噪声低、调速范围宽，高中低三档风量区别明显等特点。<span></span></span>\r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:宋体;\">盘管<span></span></span></b>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">采用铜管套铝片结构，采用机械胀管工艺，使管片结合紧密，其翅片形式是采用开利第三代最新片型，带有等边三角形孔距的宽缝波纹片，因此传热性能显著提高，冷量优于国内外同类产品。<span></span></span>\r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:宋体;\">水盘<span></span></span></b>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">采用冷轧钢板材料粉末涂装，整体模压一次成型工艺，质量可靠。水盘保温材料采用成型模一次压制而成，外形美观。<span></span></span>\r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:宋体;\">箱体<span></span></span></b>\r\n</p>\r\n<p style=\"text-indent:26.25pt;\">\r\n	<span style=\"font-family:宋体;\">采用进口<span>CNC</span>、<span>LVD</span>数控设备加工，其加工零件精度高、互换性好。<span><span>&nbsp;&nbsp;&nbsp; </span></span></span>\r\n</p>\r\n<p style=\"text-indent:26.25pt;\">\r\n	<span style=\"font-family:宋体;\">另外选用专用接线盒和金属软管，大大提高了机组安全可靠性。<span></span></span>\r\n</p>','/upfiles/image/21215435.jpg','42系列风机盘管机组，其冷热源由外界集中供应，当冷热源提供给盘管后，室内空气由风机吸入机内，经盘管冷却（加热），再送回室内，经机组不断循环，使房间温度得到调节。','2009-02-12 22:32:48'),(21,12,'39G机组结构及技术特点','&nbsp;\r\n<p>\r\n	<b><span style=\"line-height:150%;font-family:宋体;\">箱体结构<span></span></span></b> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\"><span>&nbsp;&nbsp;&nbsp; </span>39G</span><span style=\"line-height:150%;font-family:宋体;\">系列产品从开利国外公司引进消化吸收的新型产品，按美国暖通、制冷空调工程师学会<span>(ASHRAE)</span>标准检测，具备国际先进水平。<span>39G</span>系列采用加强结构设计，该设计包括一高强度的框架及双层箱板，面板和框架全部采用模数化设计。箱体面板的厚度为<span>25mm</span>。这种箱板是由三部分组成：内、外层钢板及板间的发泡聚氨酯保温材料。面板之间的联接由一高强度铝合金框架完成，这一构架无论在接角还是周边上都从内部给予整个箱体以强度上的保证。这一构架也使空调箱外观美观大方。此外，所有的检修门也是有相同的结构，但出于密闭性的考虑，在结构上给予更多的加强。<span></span></span> \r\n</p>\r\n<p>\r\n	<b><span style=\"line-height:150%;font-family:宋体;\">高效率保温材料<span></span></span></b> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\"><span>&nbsp;&nbsp;&nbsp; </span></span><span style=\"line-height:150%;font-family:宋体;\">面板中的保温材料为发泡聚氨酯保温材料，厚度为<span>25mm</span>。其热导率如下：<span> </span></span>\r\n</p>\r\n<div align=\"center\">\r\n	<table style=\"border-collapse:collapse;\" class=\"ke-zeroborder\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n		<tbody>\r\n			<tr>\r\n				<td style=\"border-bottom:windowtext 1pt solid;border-left:windowtext 1pt solid;border-top:windowtext 1pt solid;border-right:windowtext 1pt solid;\" valign=\"top\" width=\"216\">\r\n					<p>\r\n						<span style=\"line-height:150%;font-family:宋体;\">密度<span>kg/m<sup>3</sup></span></span> \r\n					</p>\r\n				</td>\r\n				<td style=\"border-bottom:windowtext 1pt solid;border-left:windowtext 1pt solid;border-top:windowtext 1pt solid;border-right:windowtext 1pt solid;\" valign=\"top\" width=\"252\">\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"line-height:150%;font-family:宋体;\">42</span> \r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td style=\"border-bottom:windowtext 1pt solid;border-left:windowtext 1pt solid;border-top:windowtext 1pt solid;border-right:windowtext 1pt solid;\" valign=\"top\" width=\"216\">\r\n					<p>\r\n						<span style=\"line-height:150%;font-family:宋体;\">热导率<span>W/m</span>℃<span></span></span> \r\n					</p>\r\n				</td>\r\n				<td style=\"border-bottom:windowtext 1pt solid;border-left:windowtext 1pt solid;border-top:windowtext 1pt solid;border-right:windowtext 1pt solid;\" valign=\"top\" width=\"252\">\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"line-height:150%;font-family:宋体;\">0.0199</span> \r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p style=\"text-indent:14.25pt;\">\r\n	<span style=\"line-height:150%;font-family:宋体;\">注 ：表中热导率为平均值，测试温度<span>20</span>℃</span><span style=\"line-height:150%;font-family:\'Kino MT\';\"><span>±</span></span><span style=\"line-height:150%;font-family:宋体;\">3</span><span style=\"line-height:150%;font-family:宋体;\">℃<span></span></span> \r\n</p>\r\n<p>\r\n	<b><span style=\"line-height:150%;font-family:宋体;\">风量范围宽<span></span></span></b> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\"><span>&nbsp;&nbsp;&nbsp; </span></span><span style=\"line-height:150%;font-family:宋体;\">本空调箱有<span>19</span>个规格机组，每个规格的机组都有二种以上风机型号可供选择，所有规格的机组在盘管面风速为<span> 2.5 m/s</span>时，覆盖了从<span> 2000 m<sup>3</sup>/h </span>到<span>60000m<sup>3</sup></span><span>/h</span>的风量范围。<span></span></span> \r\n</p>\r\n<p>\r\n	<b><span style=\"line-height:150%;font-family:宋体;\">灵活性好，易于保养<span></span></span></b> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\"><span>&nbsp;&nbsp;&nbsp; </span></span><span style=\"line-height:150%;font-family:宋体;\">极具灵活性的模数化结构体系是基于标准尺寸的面板和框架结构。标准的检修门，可迅速拆卸的面板，风门和过滤器等，使机组可根据用户的特殊要求任意调整组合。这种灵活性很强的结构给在比较困难的场合进行现场安装带来诸多方便。<span></span></span> \r\n</p>\r\n<p>\r\n	<b><span style=\"line-height:150%;font-family:宋体;\">电脑选型<span></span></span></b> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\"><span>&nbsp;&nbsp;&nbsp; </span></span><span style=\"line-height:150%;font-family:宋体;\">开利公司开发的电脑选型程序，在您需要服务时，只需提供我们所需的数据，开利的<span>AHU-DESIGNER</span>电脑程序即可提出一快速精确的选型结果。告诉您最理想的匹配，所有规格均依照<span>ARI</span>标准，保证运行性能达到最理想状态。<span></span></span> \r\n</p>\r\n<p>\r\n	<b><span style=\"line-height:150%;font-family:宋体;\">风机段<span></span></span></b> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\">A </span><span style=\"line-height:150%;font-family:宋体;\">、风机采用前向多翼和后弯叶片双进双宽低噪声离心风机。前向多翼和后弯有<span>16</span>种型号<span>(180~1000)</span>。叶轮采用高强度钢材，所有叶轮均经过静平衡和动平衡测试，风机轴承采用日本<span>NSK</span>轴承。<span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\">B</span><span style=\"line-height:150%;font-family:宋体;\">、 采用高效率电机，安装于风机段内，隔绝空气中的尘埃，使其在洁净且低温的空气中运转，有效降低电机耗电量并延长电机寿命。<span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\">C </span><span style=\"line-height:150%;font-family:宋体;\">、电机和风机置于一公用底盘上，并在底盘下设置减震器，减小振动至最低限度。电机和风机的相对位置可由电机底盘调节装置调整至最佳。风机和出风口用挠性连接，隔绝了电机和风机产生的振动等向箱体其它部分的传递。<span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\">D</span><span style=\"line-height:150%;font-family:宋体;\">、 风机驱动采用皮带传动，配备风机皮带轮和电机皮带轮。<span></span></span> \r\n</p>\r\n<p>\r\n	<b><span style=\"line-height:150%;font-family:宋体;\">盘管段<span></span></span></b> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\">A </span><span style=\"line-height:150%;font-family:宋体;\">、盘管工作压力为<span>1.6MPa </span>，而测试压力高达<span>2.8MPa</span>，依据美国空调制冷协会<span>(ARI)</span>标准制造，盘管的能量、风量、压降均符合<span>ARI</span>标准，保证最高品质。盘管铝翅片为双正弦波纹，是开利公司九十年代最新的第三代翅片。翅片和铜管用机械加压胀接，盘管四周有镀锌钢板框，钢联箱，进出水口均为螺纹接管。凝水盘置于支承导轨上，便于抽出。水盘下粘贴有发泡聚乙烯保温材料，保证无冷凝水沉积。<span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\">B</span><span style=\"line-height:150%;font-family:宋体;\">、 所有盘管均采用逆流方式安装。<span></span></span> \r\n</p>\r\n<p>\r\n	<b><span style=\"line-height:150%;font-family:宋体;\">过滤段<span></span></span></b> \r\n</p>\r\n<p style=\"text-indent:26.25pt;\">\r\n	<span style=\"line-height:150%;font-family:宋体;\">提供初效板式、中效袋式过滤器、高中效袋式过滤器和亚高效过滤器。初效主要用于过滤<span>10</span>－<span>100</span></span><span style=\"line-height:150%;font-family:Symbol;\"><span>m</span></span><span style=\"line-height:150%;font-family:宋体;\">m</span><span style=\"line-height:150%;font-family:宋体;\">的大颗粒，中效用于过滤<span>1</span>－<span>10</span></span><span style=\"line-height:150%;font-family:Symbol;\"><span>m</span></span><span style=\"line-height:150%;font-family:宋体;\">m</span><span style=\"line-height:150%;font-family:宋体;\">的中等颗粒，亚高效主要用于过滤更为细小的微粒。过滤效率规格：初效<span>G3</span>，中效<span>F5</span>，高中效<span>F7</span>，亚高效<span>F9</span>。本公司提供过滤器所用材料为耐水洗型化纤滤料<span>(</span>俗称无纺布<span>)</span>或玻璃纤维，滤材及滤框具有防腐性，便于拆卸及反复清洗使用。 <span></span></span>\r\n</p>','/upfiles/image/21215614.jpg','39G系列产品从开利国外公司引进消化吸收的新型产品，按美国暖通、制冷空调工程师学会(ASHRAE)标准检测，具备国际先进水平。','2009-02-12 22:33:02'),(22,12,'39CBF机组结构及技术特点','&nbsp;\r\n<p style=\"text-indent:19.65pt;\">\r\n	<span style=\"line-height:150%;font-family:宋体;\">39CBF</span><span style=\"line-height:150%;font-family:宋体;\">系列产品是吸取国际上最新的设计技术、材料、工艺而开发的一种新型空气处理设备，参考欧洲<span>EUROVENT</span>标准检测，具备国际先进水平。<span></span></span>\r\n</p>\r\n<p style=\"text-indent:19.65pt;\">\r\n	<span style=\"line-height:150%;font-family:宋体;\">39CBF</span><span style=\"line-height:150%;font-family:宋体;\">系列采用加强结构设计，该设计包括一高强度的钢型材框架及双层箱板，面板和框架全部采用模数化设计。箱体厚度为<span>50mm</span>。这种箱板是由三部分组成：内、外层钢板及板间的发泡聚氨酯保温材料。<span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\">1</span><span style=\"line-height:150%;font-family:宋体;\">、良好的气密性<span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\">CBF</span><span style=\"line-height:150%;font-family:宋体;\">机组的箱体由面板、框架及密封条组成，面板采用了独特的嵌入对接方式，使其连接处能非常精确地连接在一起。框架与面板之间采用密封条进行密封，而且机组的各检修门和各管道穿过处均经过精心的密封设计，使用汽车用密封条保证了机组良好的气密性。<span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\">2</span><span style=\"line-height:150%;font-family:宋体;\">、绝佳的隔热性<span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\">机组面板中间夹有<span>50mm</span>厚的聚氨酯保温材料，刚性好，甚至在框架中间也添加聚氨酯材料。并且在每个连接处都进行了特殊隔热处理，杜绝冷桥的出现。面板外表面经过特殊喷涂处理，确保了机组良好的防火与防腐蚀性能。<span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\">箱体各项性能指标如下（参照欧洲<span>EN1886</span>标准）<span></span></span>\r\n</p>\r\n<div align=\"center\">\r\n	<table style=\"border-bottom:medium none;border-left:medium none;border-collapse:collapse;border-top:medium none;border-right:medium none;\" border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n		<tbody>\r\n			<tr>\r\n				<td style=\"border-bottom:windowtext 1pt solid;border-left:windowtext 1pt solid;border-top:windowtext 1pt solid;border-right:windowtext 1pt solid;\" valign=\"top\" width=\"207\">\r\n					<p>\r\n						<span style=\"line-height:150%;font-family:宋体;\">漏风率<span></span></span>\r\n					</p>\r\n				</td>\r\n				<td style=\"border-bottom:windowtext 1pt solid;border-left:windowtext 1pt solid;border-top:windowtext 1pt solid;border-right:windowtext 1pt solid;\" valign=\"top\" width=\"261\">\r\n					<p>\r\n						<span style=\"line-height:150%;font-family:宋体;\">B</span><span style=\"line-height:150%;font-family:宋体;\">级<span></span></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td style=\"border-bottom:windowtext 1pt solid;border-left:windowtext 1pt solid;border-top:windowtext 1pt solid;border-right:windowtext 1pt solid;\" valign=\"top\" width=\"207\">\r\n					<p>\r\n						<span style=\"line-height:150%;font-family:宋体;\">冷桥因子<span></span></span>\r\n					</p>\r\n				</td>\r\n				<td style=\"border-bottom:windowtext 1pt solid;border-left:windowtext 1pt solid;border-top:windowtext 1pt solid;border-right:windowtext 1pt solid;\" valign=\"top\" width=\"261\">\r\n					<p>\r\n						<span style=\"line-height:150%;font-family:宋体;\">TB2</span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td style=\"border-bottom:windowtext 1pt solid;border-left:windowtext 1pt solid;border-top:windowtext 1pt solid;border-right:windowtext 1pt solid;\" valign=\"top\" width=\"207\">\r\n					<p>\r\n						<span style=\"line-height:150%;font-family:宋体;\">热传导率<span></span></span>\r\n					</p>\r\n				</td>\r\n				<td style=\"border-bottom:windowtext 1pt solid;border-left:windowtext 1pt solid;border-top:windowtext 1pt solid;border-right:windowtext 1pt solid;\" valign=\"top\" width=\"261\">\r\n					<p>\r\n						<span style=\"line-height:150%;font-family:宋体;\">T2</span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\">高效率保温材料<span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\"><span>&nbsp;&nbsp;&nbsp; </span></span><span style=\"line-height:150%;font-family:宋体;\">面板中的保温材料为发泡聚氨酯保温材料，厚度为<span>50mm</span>。其热导率如下：<span> </span></span>\r\n</p>\r\n<div align=\"center\">\r\n	<table style=\"border-collapse:collapse;\" class=\"ke-zeroborder\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n		<tbody>\r\n			<tr>\r\n				<td style=\"border-bottom:windowtext 1pt solid;border-left:windowtext 1pt solid;border-top:windowtext 1pt solid;border-right:windowtext 1pt solid;\" valign=\"top\" width=\"216\">\r\n					<p>\r\n						<span style=\"line-height:150%;font-family:宋体;\">密度<span>kg/m3</span></span>\r\n					</p>\r\n				</td>\r\n				<td style=\"border-bottom:windowtext 1pt solid;border-left:windowtext 1pt solid;border-top:windowtext 1pt solid;border-right:windowtext 1pt solid;\" valign=\"top\" width=\"252\">\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"line-height:150%;font-family:宋体;\">42</span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td style=\"border-bottom:windowtext 1pt solid;border-left:windowtext 1pt solid;border-top:windowtext 1pt solid;border-right:windowtext 1pt solid;\" valign=\"top\" width=\"216\">\r\n					<p>\r\n						<span style=\"line-height:150%;font-family:宋体;\">热导率<span>W/m</span>℃<span></span></span>\r\n					</p>\r\n				</td>\r\n				<td style=\"border-bottom:windowtext 1pt solid;border-left:windowtext 1pt solid;border-top:windowtext 1pt solid;border-right:windowtext 1pt solid;\" valign=\"top\" width=\"252\">\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"line-height:150%;font-family:宋体;\">0.0199</span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p style=\"text-indent:14.25pt;\">\r\n	<span style=\"line-height:150%;font-family:宋体;\">注 ：表中热导率为平均值，测试温度<span>20</span>℃</span><span style=\"line-height:150%;font-family:\'Kino MT\';\"><span>±</span></span><span style=\"line-height:150%;font-family:宋体;\">3</span><span style=\"line-height:150%;font-family:宋体;\">℃<span></span></span>\r\n</p>\r\n<p style=\"text-indent:14.25pt;\">\r\n	<span style=\"line-height:150%;font-family:宋体;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\">风量范围宽<span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\"><span>&nbsp;&nbsp;&nbsp; </span></span><span style=\"line-height:150%;font-family:宋体;\">本空调箱有<span>19</span>个规格机组，每个规格的机组都有一、二种风机型号可供选择，所有规格的机组在盘管面风速为<span> 2.5 m/s</span>时，覆盖了从<span> 2000 m3/h </span>到<span>60000m3</span><span>/h</span>的风量范围。<span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\">灵活性好，易于保养<span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\"><span>&nbsp;&nbsp;&nbsp; </span></span><span style=\"line-height:150%;font-family:宋体;\">极具灵活性的模数化结构体系是基于标准尺寸的面板和框架结构。标准的检修门，可迅速拆卸的面板，风门和过滤器等，使机组可根据用户的特殊要求任意调整组合。这种灵活性很强的结构给在比较困难的场合进行现场安装带来诸多方便。<span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\">电脑选型<span></span></span>\r\n</p>\r\n<p style=\"text-indent:12pt;\">\r\n	<span style=\"line-height:150%;font-family:宋体;\"><span>&nbsp;</span></span><span style=\"line-height:150%;font-family:宋体;\">开利公司开发的电脑选型程序，在您需要服务时，只需提供我们所需的数据，开利的<span>AHU-DESIGNER</span>电脑程序即可得出一快速精确的选型结果。告诉您最理想的匹配，所有规格均依照<span>ARI</span>标准，保证运行性能达到最理想状态。<span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\">应用场合<span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\">各种舒适性和工业性空调，如烟厂、医院、展览馆等。<span></span></span>\r\n</p>\r\n<p>\r\n	<b><span style=\"line-height:150%;font-family:宋体;\">风机段<span></span></span></b>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\">A </span><span style=\"line-height:150%;font-family:宋体;\">、风机采用前向多翼和后弯叶片双进双宽低噪声离心风机。前向多翼和后弯有<span>16</span>种型号<span>(180~1000)</span>。风机轴承采用日本<span>NSK</span>轴承。叶轮采用高强度钢材，所有叶轮、皮带轮均经过静平衡和动平衡测试校正，整体风机再经运行振动测试校正，运转平稳。<span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\">B</span><span style=\"line-height:150%;font-family:宋体;\">、 采用高效率电机，安装于风机段内，隔绝空气中的尘埃，使其在洁净且低温的空气中运转，有效降低电机耗电量并延长电机寿命。<span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\">C </span><span style=\"line-height:150%;font-family:宋体;\">、电机和风机置于一公用底盘上，并在底盘下设置减震器，减小振动至最低限度。电机和风机的相对位置可由电机底盘调节装置调整至最佳。风机和出风口用挠性连接，隔绝了电机和风机产生的振动等向箱体其它部分的传递。<span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\">D</span><span style=\"line-height:150%;font-family:宋体;\">、 风机驱动采用皮带传动，配备风机皮带轮和电机皮带轮。<span></span></span>\r\n</p>\r\n<p>\r\n	<b><span style=\"line-height:150%;font-family:宋体;\">盘管段<span></span></span></b>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\">A </span><span style=\"line-height:150%;font-family:宋体;\">、盘管工作压力为<span>1.6MPa </span>，而测试压力高达<span>2.8MPa</span>，依据美国空调制冷协会<span>(ARI)</span>标准制造，盘管的能量、风量、压降均符合<span>ARI</span>标准，保证最高品质。盘管铝翅片为双正弦波纹，是开利公司九十年代最新的第三代翅片。翅片用机械加压胀接于铜管上，传热效率极佳。盘管钢联箱上设有排水阀和放气阀，保证回路中无空气积聚，同时冬季盘管内可放尽积水。进出水口均为螺纹接管，便于水管连接。凝水盘置于支承导轨上，便于抽出。水盘下粘贴有聚苯乙烯保温材料，保证无冷凝水沉积。<span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\">B</span><span style=\"line-height:150%;font-family:宋体;\">、 所有盘管均采用逆流方式。<span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:150%;font-family:宋体;\">C</span><span style=\"line-height:150%;font-family:宋体;\">、当迎风面速大于<span>2.5m</span><span>/s</span>时，盘管后设置挡水器有效隔离空气中的水分。<span></span></span>\r\n</p>\r\n<p>\r\n	<b><span style=\"line-height:150%;font-family:宋体;\">过滤段<span></span></span></b>\r\n</p>\r\n<p style=\"text-indent:26.25pt;\">\r\n	<span style=\"line-height:150%;font-family:宋体;\">提供初效板式、中效袋式过滤器、高中效袋式过滤器和亚高效过滤器等。初效主要用于过滤<span>10</span>－<span>100</span></span><span style=\"line-height:150%;font-family:Symbol;\"><span>m</span></span><span style=\"line-height:150%;font-family:宋体;\">m</span><span style=\"line-height:150%;font-family:宋体;\">的大颗粒，中效用于过滤<span>1</span>－<span>10</span></span><span style=\"line-height:150%;font-family:Symbol;\"><span>m</span></span><span style=\"line-height:150%;font-family:宋体;\">m</span><span style=\"line-height:150%;font-family:宋体;\">的中等颗粒，亚高效主要用于过滤更为细小的微粒。过滤效率规格：初效<span>G3~G4</span>，中效<span>F5~F8</span>，亚高效<span>F9</span>。本公司提供过滤器所用材料为耐水洗型化纤滤料<span>(</span>俗称无纺布<span>)</span>或玻璃纤维，滤材及滤框具有防腐性，便于拆卸及反复清洗使用。<span></span></span>\r\n</p>','/upfiles/image/21215735.jpg','39CBF系列产品是吸取国际上最新的设计技术、材料、工艺而开发的一种新型空气处理设备，参考欧洲EUROVENT标准检测，具备国际先进水平。','2009-02-12 22:33:19'),(23,1,'30RB/RQ系列涡旋式风冷冷水（热泵）机组技术特点','','/upfiles/image/212151556.jpg','高效无氯的HFC-410A制冷剂，高性能涡旋压缩机，调节精确灵敏的电子膨胀阀，第四代“飞鸟TM”低噪音轴流风扇，自适应的Pro-Dialog Plus微处理控制系统、内置式水力模块等。','2009-05-07 23:38:41'),(24,1,'19XR系列离心式冷水机组技术特点','','/upfiles/image/21215299.jpg','19XR系列封闭型离心式冷水机组是开利公司二十世纪九十年代末推出的最新一代离心式冷水机组。','2009-02-12 22:29:09'),(25,1,'16DEH系列蒸汽型双效吸收式冷水机组特点','','/upfiles/image/212153039.jpg','机组注重可靠性设计，机组上配置的各种控制模块、传感元件，以及各种关键材料等均采用享誉全球的开利离心机组和螺杆机组成熟部件','2009-05-07 23:36:04'),(26,23,'SMC','SMC','/upfiles/image/21215423.jpg','无','2009-02-12 22:42:03'),(27,24,'北京航空航天大学举重馆','北京航空航天大学举重馆','/upfiles/image/21215460.jpg','北京航空航天大学举重馆','2009-02-12 22:46:00'),(28,20,'北京人大常委会','北京人大常委会','/upfiles/image/212154917.jpg','无','2009-02-12 22:49:17'),(29,20,'博大大厦','博大大厦','/upfiles/image/21215506.jpg','无','2009-02-12 22:50:06'),(30,20,'长安戴斯大饭店','长安戴斯大饭店','/upfiles/image/212155123.jpg','无','2009-02-12 22:51:23'),(31,23,'崇文门饭店','崇文门饭店','/upfiles/image/21215527.jpg','无','2009-02-12 22:52:07'),(32,31,'维修保养-1','维修保养-1','/upfiles/image/216115442.jpg','无','2009-03-24 20:59:32'),(33,31,'维修保养2','维修保养2','/upfiles/image/216115513.jpg','无','2009-03-24 21:00:32'),(34,23,'崇文区国税局','崇文区国税局','/upfiles/image/212155731.jpg','崇文区国税局','2009-02-12 22:57:31'),(35,23,'大宝饭店','大宝饭店','/upfiles/image/212155812.jpg','大宝饭店','2009-03-20 18:53:08'),(36,23,'大康大厦','大康大厦','/upfiles/image/21216819.jpg','无','2009-02-12 23: